namespace Polycode.NostalgicPlayer.Ports.FFmpeg.LibAvFormat.Demuxer.Containers
{
	/// <summary>
	/// 
	/// </summary>
	internal class AsfMainHeader
	{
		/// <summary>
		/// Generated by client computer
		/// </summary>
		public FF_Asf_Guid Guid = new FF_Asf_Guid();

		/// <summary>
		/// In bytes
		/// Invalid if broadcasting
		/// </summary>
		public uint64_t File_Size;

		/// <summary>
		/// Time of creation, in 100-nanosecond units since 1.1.1601
		/// Invalid if broadcasting
		/// </summary>
		public uint64_t Create_Time;

		/// <summary>
		/// Play time, in 100-nanosecond units
		/// Invalid if broadcasting
		/// </summary>
		public uint64_t Play_Time;

		/// <summary>
		/// Time to send file, in 100-nanosecond units
		/// Invalid if broadcasting (could be ignored)
		/// </summary>
		public uint64_t Send_Time;

		/// <summary>
		/// Timestamp of the first packet, in milliseconds
		/// If nonzero - subtract from time
		/// </summary>
		public uint32_t Preroll;

		/// <summary>
		/// Preroll is 64 bits - but let's just ignore it
		/// </summary>
		public uint32_t Ignore;

		/// <summary>
		/// 0x01 - broadcast
		/// 0x02 - seekable
		/// Rest is reserved should be 0
		/// </summary>
		public uint32_t Flags;

		/// <summary>
		/// Size of a data packet
		/// Invalid if broadcasting
		/// </summary>
		public uint32_t Min_PktSize;

		/// <summary>
		/// Shall be the same as for min_pktsize
		/// Invalid if broadcasting
		/// </summary>
		public uint32_t Max_PktSize;

		/// <summary>
		/// Bandwidth of stream in bps
		/// Should be the sum of bitrates of the
		/// individual media streams 
		/// </summary>
		public uint32_t Max_BitRate;
	}
}
